import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { Container, Row, Col } from "reactstrap";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";

import "../styles/checkout.css";

const Checkout = () => {
  const [shippingInfo, setShippingInfo] = useState({
    name: "",
    email: "",
    phone: "",
    country: "",
    city: "",
    postalCode: "",
    cardNumber: "",
    cardHolder: "",
    expirationDate: "",
    cvv: "",
  });
  const [errors, setErrors] = useState({});
  const stripe = useStripe();
  const elements = useElements();
  const cartTotalAmount = useSelector((state) => state.cart.totalAmount);
  const shippingCost = 30;

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form fields
    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    // Create a payment method using Stripe Elements
    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: elements.getElement(CardElement),
    });

    if (error) {
      console.error("Error creating payment method:", error);
    } else {
      // Payment method created successfully, proceed with payment
      const { id } = paymentMethod;
      // Your payment processing logic here
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setShippingInfo({
      ...shippingInfo,
      [name]: value,
    });
  };

  const validateForm = () => {
    const errors = {};
    // Your validation logic for shipping information
    return errors;
  };

  const calculateTotalAmount = () => {
    // Calculate total amount including shipping cost
    return cartTotalAmount + Number(shippingCost);
  };

  return (
    <Container>
      <Row>
        <Col lg="8" md="6">
          <h6 className="mb-4">Enter your Shipping Details</h6>
          <form className="payment__form" onSubmit={handleSubmit}>
            {/* Your shipping details form fields */}
            {/* Card Element for Stripe payment */}
            <div id="payment-element">
              <CardElement />
            </div>
            <button type="submit" className="payment__btn">
              Pay Now
            </button>
          </form>
        </Col>
        <Col lg="4" md="6">
          <div className="checkout__bill">
            {/* Your billing details */}
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Checkout;
