import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { Container, Row, Col } from "reactstrap";
import { loadStripe } from "@stripe/stripe-js";

import CommonSection from "../components/UI/common-section/CommonSection";
import Helmet from "../components/Helmet/Helmet";

import "../styles/checkout.css";

const Checkout = () => {
  const [stripe, setStripe] = useState(null);
  const [paymentIntent, setPaymentIntent] = useState(null);
  const [paymentError, setPaymentError] = useState(null);

  const cartTotalAmount = useSelector((state) => state.cart.totalAmount);
  const shippingCost = 30;
  const totalAmount = cartTotalAmount + Number(shippingCost);

  useEffect(() => {
    // Initialize Stripe
    const initializeStripe = async () => {
      const stripeObj = await loadStripe("your_publishable_key");
      setStripe(stripeObj);
    };

    initializeStripe();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Call your backend to create a payment intent
      const response = await fetch("/create-payment-intent", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ amount: totalAmount }),
      });

      const data = await response.json();

      // Confirm the payment intent
      const { client_secret } = data;
      const result = await stripe.confirmCardPayment(client_secret, {
        payment_method: {
          card: stripe.elements.getElement("card"),
          billing_details: {
            name: e.target.cardHolder.value,
          },
        },
      });

      if (result.error) {
        setPaymentError(result.error.message);
      } else {
        localStorage.setItem("shippingInfo", JSON.stringify(shippingInfo));
        alert("Payment successful!");
      }
    } catch (error) {
      console.error("Error:", error);
      setPaymentError("An error occurred while processing your payment.");
    }
  };

  return (
    <Helmet title="Checkout">
      <CommonSection title="Payment Information" />
      <section>
        <Container>
          <Row>
            <Col lg="8" md="6">
              <h6 className="mb-4">Enter your Shipping Details</h6>
              <form className="payment__form" onSubmit={handleSubmit}>
                {/* Your form fields */}
              </form>
            </Col>
            <Col lg="4" md="6">
              <div className="checkout__bill">
                {/* Your checkout summary */}
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </Helmet>
  );
};

export default Checkout;
