import React, { useState } from "react";
import { useSelector } from "react-redux";
import { Container, Row, Col } from "reactstrap";
import CommonSection from "../components/UI/common-section/CommonSection";
import Helmet from "../components/Helmet/Helmet";

import "../styles/checkout.css";

const Checkout = () => {
  const [shippingInfo, setShippingInfo] = useState({
    cardNumber: "",
    cardHolder: "",
    expirationDate: "",
    cvv: "",
  });

  const [errors, setErrors] = useState({});

  const cartTotalAmount = useSelector((state) => state.cart.totalAmount);
  const shippingCost = 30;

  const totalAmount = cartTotalAmount + Number(shippingCost);

  const handleSubmit = (e) => {
    e.preventDefault();
    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length === 0) {
      // Proceed with submission
      localStorage.setItem("shippingInfo", JSON.stringify(shippingInfo));
      alert("Payment information saved!");
    } else {
      // Display validation errors
      setErrors(validationErrors);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setShippingInfo({
      ...shippingInfo,
      [name]: value,
    });
  };

  const validateForm = () => {
    const errors = {};

    if (!shippingInfo.cardNumber || shippingInfo.cardNumber.length !== 16 || isNaN(shippingInfo.cardNumber)) {
      errors.cardNumber = "Invalid card number";
    }

    if (!shippingInfo.cardHolder) {
      errors.cardHolder = "Card holder name is required";
    }

    if (!shippingInfo.expirationDate || shippingInfo.expirationDate.length !== 5 || !/^\d{2}\/\d{2}$/.test(shippingInfo.expirationDate)) {
      errors.expirationDate = "Invalid expiration date (MM/YY)";
    }

    if (!shippingInfo.cvv || shippingInfo.cvv.length !== 3 || isNaN(shippingInfo.cvv)) {
      errors.cvv = "Invalid CVV";
    }

    return errors;
  };

  return (
    <Helmet title="Checkout">
      <CommonSection title="Payment Information" />
      <section>
        <Container>
          <Row>
            <Col lg="8" md="6">
              <h6 className="mb-4">Enter your Payment Details</h6>
              <form className="payment__form" onSubmit={handleSubmit}>
                <div className="form__group">
                  <input
                    type="text"
                    placeholder="Card Number"
                    name="cardNumber"
                    required
                    value={shippingInfo.cardNumber}
                    onChange={handleChange}
                  />
                  {errors.cardNumber && <div className="error">{errors.cardNumber}</div>}
                </div>

                <div className="form__group">
                  <input
                    type="text"
                    placeholder="Card Holder Name"
                    name="cardHolder"
                    required
                    value={shippingInfo.cardHolder}
                    onChange={handleChange}
                  />
                  {errors.cardHolder && <div className="error">{errors.cardHolder}</div>}
                </div>

                <div className="form__group">
                  <input
                    type="text"
                    placeholder="Expiration Date (MM/YY)"
                    name="expirationDate"
                    required
                    value={shippingInfo.expirationDate}
                    onChange={handleChange}
                  />
                  {errors.expirationDate && <div className="error">{errors.expirationDate}</div>}
                </div>

                <div className="form__group">
                  <input
                    type="text"
                    placeholder="CVV"
                    name="cvv"
                    required
                    value={shippingInfo.cvv}
                    onChange={handleChange}
                  />
                  {errors.cvv && <div className="error">{errors.cvv}</div>}
                </div>

                <button type="submit" className="payment__btn">
                  Pay Now
                </button>
              </form>
            </Col>

            <Col lg="4" md="6">
              <div className="checkout__bill">
                <h6 className="d-flex align-items-center justify-content-between mb-3">
                  Subtotal: <span>${cartTotalAmount}</span>
                </h6>
                <h6 className="d-flex align-items-center justify-content-between mb-3">
                  Shipping Cost: <span>${shippingCost}</span>
                </h6>
                <div className="checkout__total">
                  <h5 className="d-flex align-items-center justify-content-between">
                    Total: <span>${totalAmount}</span>
                  </h5>
                </div>
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </Helmet>
  );
};

export default Checkout;
