import React, { useState } from "react";
import { useSelector } from "react-redux";
import { Container, Row, Col } from "reactstrap";
import CommonSection from "../components/UI/common-section/CommonSection";
import Helmet from "../components/Helmet/Helmet";

import "../styles/checkout.css";

const Checkout = () => {
  const [shippingInfo, setShippingInfo] = useState({
    name: "",
    email: "",
    phone: "",
    country: "",
    city: "",
    postalCode: "",
  });

  const cartTotalAmount = useSelector((state) => state.cart.totalAmount);
  const shippingCost = 30;

  const totalAmount = cartTotalAmount + Number(shippingCost);

  const submitHandler = (e) => {
    e.preventDefault();
    // Store shippingInfo in local storage
    localStorage.setItem("shippingInfo", JSON.stringify(shippingInfo));
    // Redirect to payment page or perform any other necessary actions
    // For demonstration purposes, let's alert that the information is saved
    alert("Shipping information saved!");
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setShippingInfo({
      ...shippingInfo,
      [name]: value,
    });
  };

  return (
    <Helmet title="Checkout">
      <CommonSection title="Finaliser la commande" />
      <section>
        <Container>
          <Row>
            <Col lg="8" md="6">
              <h6 className="mb-4">Adresse de livraison</h6>
              <form className="checkout__form" onSubmit={submitHandler}>
                <div className="form__group">
                  <input
                    type="text"
                    placeholder="Entrez votre nom"
                    name="name"
                    required
                    value={shippingInfo.name}
                    onChange={handleChange}
                  />
                </div>

                <div className="form__group">
                  <input
                    type="email"
                    placeholder="Entrer votre email"
                    name="email"
                    required
                    value={shippingInfo.email}
                    onChange={handleChange}
                  />
                </div>
                <div className="form__group">
                  <input
                    type="number"
                    placeholder="Numéro de téléphone"
                    name="phone"
                    required
                    value={shippingInfo.phone}
                    onChange={handleChange}
                  />
                </div>
                <div className="form__group">
                  <input
                    type="text"
                    placeholder="Pays"
                    name="country"
                    required
                    value={shippingInfo.country}
                    onChange={handleChange}
                  />
                </div>
                <div className="form__group">
                  <input
                    type="text"
                    placeholder="Ville"
                    name="city"
                    required
                    value={shippingInfo.city}
                    onChange={handleChange}
                  />
                </div>
                <div className="form__group">
                  <input
                    type="number"
                    placeholder="Code Postal"
                    name="postalCode"
                    required
                    value={shippingInfo.postalCode}
                    onChange={handleChange}
                  />
                </div>
                <button type="submit" className="addTOCart__btn">
                  Paiement
                </button>
              </form>
            </Col>

            <Col lg="4" md="6">
              <div className="checkout__bill">
                <h6 className="d-flex align-items-center justify-content-between mb-3">
                  Sous-total: <span>${cartTotalAmount}</span>
                </h6>
                <h6 className="d-flex align-items-center justify-content-between mb-3">
                  Frais de livraison: <span>${shippingCost}</span>
                </h6>
                <div className="checkout__total">
                  <h5 className="d-flex align-items-center justify-content-between">
                    Total: <span>${totalAmount}</span>
                  </h5>
                </div>
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </Helmet>
  );
};

export default Checkout;
