import React, { useState } from "react";
import { Container, Row, Col } from "reactstrap";
import CommonSection from "../components/UI/common-section/CommonSection";
import Helmet from "../components/Helmet/Helmet";

import "../styles/payment.css";

const Payment = () => {
  const [paymentInfo, setPaymentInfo] = useState({
    cardNumber: "",
    cardHolder: "",
    expirationDate: "",
    cvv: "",
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    // Send payment information to backend or perform any other necessary actions
    // For demonstration purposes, let's alert that the payment information is saved
    alert("Payment information saved!");
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPaymentInfo({
      ...paymentInfo,
      [name]: value,
    });
  };

  return (
    <Helmet title="Payment">
      <CommonSection title="Payment Information" />
      <section>
        <Container>
          <Row>
            <Col lg="8" md="6">
              <h6 className="mb-4">Enter your Payment Details</h6>
              <form className="payment__form" onSubmit={handleSubmit}>
                <div className="form__group">
                  <input
                    type="text"
                    placeholder="Card Number"
                    name="cardNumber"
                    required
                    value={paymentInfo.cardNumber}
                    onChange={handleChange}
                  />
                </div>

                <div className="form__group">
                  <input
                    type="text"
                    placeholder="Card Holder Name"
                    name="cardHolder"
                    required
                    value={paymentInfo.cardHolder}
                    onChange={handleChange}
                  />
                </div>

                <div className="form__group">
                  <input
                    type="text"
                    placeholder="Expiration Date (MM/YY)"
                    name="expirationDate"
                    required
                    value={paymentInfo.expirationDate}
                    onChange={handleChange}
                  />
                </div>

                <div className="form__group">
                  <input
                    type="text"
                    placeholder="CVV"
                    name="cvv"
                    required
                    value={paymentInfo.cvv}
                    onChange={handleChange}
                  />
                </div>

                <button type="submit" className="payment__btn">
                  Pay Now
                </button>
              </form>
            </Col>
          </Row>
        </Container>
      </section>
    </Helmet>
  );
};

export default Payment;
