import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { Container, Row, Col } from "reactstrap";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";

import CommonSection from "../components/UI/common-section/CommonSection";
import Helmet from "../components/Helmet/Helmet";

import "../styles/checkout.css";

const Checkout = () => {
  const [paymentError, setPaymentError] = useState(null);
  const [paymentSuccess, setPaymentSuccess] = useState(false);

  const stripe = useStripe();
  const elements = useElements();

  const cartTotalAmount = useSelector((state) => state.cart.totalAmount);
  const shippingCost = 30;
  const totalAmount = cartTotalAmount + Number(shippingCost);

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      // Stripe.js has not yet loaded.
      // Make sure to disable form submission until Stripe.js has loaded.
      return;
    }

    const cardElement = elements.getElement(CardElement);

    try {
      const result = await stripe.confirmCardPayment("{YOUR_PAYMENT_INTENT_CLIENT_SECRET}", {
        payment_method: {
          card: cardElement,
          billing_details: {
            name: event.target.cardHolder.value,
          },
        },
      });

      if (result.error) {
        setPaymentError(result.error.message);
      } else {
        // Payment successful
        setPaymentError(null);
        setPaymentSuccess(true);
        localStorage.setItem("shippingInfo", JSON.stringify(shippingInfo));
        alert("Payment successful!");
      }
    } catch (error) {
      console.error("Error:", error);
      setPaymentError("An error occurred while processing your payment.");
    }
  };

  return (
    <Helmet title="Checkout">
      <CommonSection title="Payment Information" />
      <section>
        <Container>
          <Row>
            <Col lg="8" md="6">
              <h6 className="mb-4">Enter your Shipping Details</h6>
              <form className="payment__form" onSubmit={handleSubmit}>
                {/* Your form fields */}
                <label>
                  Card details
                  <CardElement />
                </label>
                <button type="submit" disabled={!stripe}>
                  Pay Now
                </button>
              </form>
            </Col>
            <Col lg="4" md="6">
              <div className="checkout__bill">
                {/* Your checkout summary */}
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </Helmet>
  );
};

export default Checkout;
