import React, { useState } from "react";
import { useSelector } from "react-redux";
import { Container, Row, Col } from "reactstrap";
import CommonSection from "../components/UI/common-section/CommonSection";
import Helmet from "../components/Helmet/Helmet";

import "../styles/checkout.css";

const Checkout = () => {
  const [paymentInfo, setPaymentInfo] = useState({
    cardNumber: "",
    cardHolder: "",
    expirationDate: "",
    cvv: "",
  });

  const cartTotalAmount = useSelector((state) => state.cart.totalAmount);
  const shippingCost = 30;

  const totalAmount = cartTotalAmount + Number(shippingCost);

  const submitHandler = (e) => {
    e.preventDefault();
    // Store shippingInfo in local storage
    localStorage.setItem("shippingInfo", JSON.stringify(shippingInfo));
    // Redirect to payment page or perform any other necessary actions
    // For demonstration purposes, let's alert that the information is saved
    alert("Shipping information saved!");
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setShippingInfo({
      ...shippingInfo,
      [name]: value,
    });
  };

  return (
    <Helmet title="checkout">
    <CommonSection title="Payment Information" />
    <section>
      <Container>
        <Row>
          <Col lg="8" md="6">
            <h6 className="mb-4">Enter your Payment Details</h6>
            <form className="payment__form" onSubmit={handleSubmit}>
              <div className="form__group">
                <input
                  type="text"
                  placeholder="Card Number"
                  name="cardNumber"
                  required
                  value={paymentInfo.cardNumber}
                  onChange={handleChange}
                />
              </div>

              <div className="form__group">
                <input
                  type="text"
                  placeholder="Card Holder Name"
                  name="cardHolder"
                  required
                  value={paymentInfo.cardHolder}
                  onChange={handleChange}
                />
              </div>

              <div className="form__group">
                <input
                  type="text"
                  placeholder="Expiration Date (MM/YY)"
                  name="expirationDate"
                  required
                  value={paymentInfo.expirationDate}
                  onChange={handleChange}
                />
              </div>

              <div className="form__group">
                <input
                  type="text"
                  placeholder="CVV"
                  name="cvv"
                  required
                  value={paymentInfo.cvv}
                  onChange={handleChange}
                />
              </div>

              <button type="submit" className="payment__btn">
                Pay Now
              </button>
            </form>
          </Col>

            <Col lg="4" md="6">
              <div className="checkout__bill">
                <h6 className="d-flex align-items-center justify-content-between mb-3">
                  Sous-total: <span>${cartTotalAmount}</span>
                </h6>
                <h6 className="d-flex align-items-center justify-content-between mb-3">
                  Frais de livraison: <span>${shippingCost}</span>
                </h6>
                <div className="checkout__total">
                  <h5 className="d-flex align-items-center justify-content-between">
                    Total: <span>${totalAmount}</span>
                  </h5>
                </div>
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </Helmet>
  );
};

export default Checkout;
