import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { Container, Row, Col } from "reactstrap";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import axios from "axios";

import "../styles/checkout.css";

const Checkout = () => {
  const [shippingInfo, setShippingInfo] = useState({
    name: "",
    email: "",
    phone: "",
    country: "",
    city: "",
    postalCode: "",
    cardNumber: "",
    cardHolder: "",
    expirationDate: "",
    cvv: "",
  });
  const [errors, setErrors] = useState({});
  const stripe = useStripe();
  const elements = useElements();

  useEffect(() => {
    const stripePromise = loadStripe('pk_test_...');
    stripePromise.then(stripe => {
      const appearance = { /* appearance */ };
      const options = { /* options */ };
      const paymentElement = stripe.elements({ clientSecret, appearance }).create('payment', options);
      paymentElement.mount('#payment-element');
    });
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form fields
    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    // Create a payment method using Stripe Elements
    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: elements.getElement(CardElement),
    });

    if (error) {
      console.error("Error creating payment method:", error);
    } else {
      // Payment method created successfully, proceed with payment
      const { id } = paymentMethod;
      try {
        const response = await axios.post("/api/payment", {
          amount: calculateTotalAmount() * 100, // Convert amount to cents
          currency: "usd",
          payment_method: id,
        });
        if (response.data.success) {
          // Payment successful
          localStorage.setItem("shippingInfo", JSON.stringify(shippingInfo));
          alert("Payment successful!");
        }
      } catch (error) {
        console.error("Error processing payment:", error);
        alert("Payment failed. Please try again later.");
      }
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setShippingInfo({
      ...shippingInfo,
      [name]: value,
    });
  };

  const validateForm = () => {
    const errors = {};

    // Your validation logic for shipping information

    return errors;
  };

  const calculateTotalAmount = () => {
    // Calculate total amount including shipping cost
    const cartTotalAmount = useSelector((state) => state.cart.totalAmount);
    const shippingCost = 30;
    return cartTotalAmount + Number(shippingCost);
  };

  return (
    <Container>
      <Row>
        <Col lg="8" md="6">
          <h6 className="mb-4">Enter your Shipping Details</h6>
          <form className="payment__form" onSubmit={handleSubmit}>
            {/* Your shipping details form fields */}
            <div className="form__group">
              <input
                type="text"
                placeholder="Name"
                name="name"
                required
                value={shippingInfo.name}
                onChange={handleChange}
              />
              {errors.name && <div className="error">{errors.name}</div>}
            </div>
            {/* More input fields for other shipping details */}
            {/* Card Element for Stripe payment */}
            <div id="payment-element">
              <CardElement />
            </div>
            <button type="submit" className="payment__btn">
              Pay Now
            </button>
          </form>
        </Col>
        <Col lg="4" md="6">
          <div className="checkout__bill">
            {/* Your billing details */}
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Checkout;
